import { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "./ExpensesFilter";
import Card from "../UI/Card";
import "./Expenses.css";

function Expenses(props) {
  // props: items
  const [filteredYear, setFilteredYear] = useState('2021');

  const filteredExpenses = props.items.filter((expenseItem) => {
    return expenseItem.date.getFullYear().toString() === filteredYear;
  });

  function filterChangeHandler(selectedYear) {
    setFilteredYear(selectedYear);
  }

  let exoensesContent = <div/>
  if(filteredExpenses.length > 0){
    expensesContent =  filteredExpenses.map((expenseItem) => (
      <ExpenseItem
        key={expenseItem.id}
        title={expenseItem.title}
        amount={expenseItem.amount}
        date={expenseItem.date}
      />
    ))
  } else {
    expensesContent = <p> No expenses found. </p>;
  }

  return (
    <Card className="expenses">
      <ExpensesFilter
        selected={filteredYear}
        onChangeFilter={filterChangeHandler}
      />
      {expensesContent}
    </Card>
  );
}

export default Expenses;
